import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useAuth } from "@/lib/auth-context";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Avatar, AvatarFallback } from "@/components/ui/avatar";
import { Label } from "@/components/ui/label";
import { ThemeToggle } from "@/components/theme-toggle";
import { LanguageToggle } from "@/components/language-toggle";
import { Settings, Upload, X, LogOut } from "lucide-react";
import { useState, useRef, useEffect } from "react";
import type { User } from "@shared/schema";
import { useLanguage } from "@/lib/language-context";

const profileSchema = z.object({
  displayName: z.string().max(50, "Display name must be at most 50 characters").optional().or(z.literal("")),
  username: z.string().min(3, "Username must be at least 3 characters").max(20, "Username must be at most 20 characters"),
});

type ProfileFormData = z.infer<typeof profileSchema>;

export default function SettingsPage() {
  const { user, setUser } = useAuth();
  const { t } = useLanguage();
  const { toast } = useToast();
  const [previewAvatar, setPreviewAvatar] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const form = useForm<ProfileFormData>({
    resolver: zodResolver(profileSchema),
    defaultValues: {
      displayName: user?.displayName || "",
      username: user?.username || "",
    },
  });

  // Update form values when user changes
  useEffect(() => {
    if (user) {
      form.reset({
        displayName: user.displayName || "",
        username: user.username || "",
      });
    }
  }, [user, form]);

  const updateProfileMutation = useMutation({
    mutationFn: async (data: ProfileFormData & { avatarUrl?: string }) => {
      const res = await apiRequest("POST", "/api/auth/profile", data);
      return await res.json() as User;
    },
    onSuccess: (updatedUser) => {
      setUser(updatedUser);
      setPreviewAvatar(null);
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      toast({
        title: "Profile updated",
        description: "Your profile has been successfully updated.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getInitials = (username: string) => {
    return username.slice(0, 2).toUpperCase();
  };

  const getAvatarUrl = (avatarUrl: string | null | undefined, username: string) => {
    if (avatarUrl) return avatarUrl;
    return `https://ui-avatars.com/api/?name=${encodeURIComponent(username)}&background=0D8ABC&color=fff&size=128`;
  };

  const handleAvatarChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    if (file.size > 5 * 1024 * 1024) {
      toast({
        title: "File too large",
        description: "Avatar must be less than 5MB",
        variant: "destructive",
      });
      return;
    }

    const reader = new FileReader();
    reader.onload = (event) => {
      const result = event.target?.result as string;
      setPreviewAvatar(result);
    };
    reader.readAsDataURL(file);
  };

  const clearAvatar = () => {
    setPreviewAvatar(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = "";
    }
  };

  const handleSubmit = form.handleSubmit((data) => {
    updateProfileMutation.mutate({
      ...data,
      avatarUrl: previewAvatar || undefined,
    });
  });

  return (
    <div className="flex-1 flex flex-col h-full bg-background">
      <div className="p-6 border-b border-border">
        <div className="flex items-center gap-3 mb-2">
          <Settings className="h-6 w-6 text-primary" />
          <h1 className="text-2xl font-semibold">{t('settings.settings')}</h1>
        </div>
        <p className="text-sm text-muted-foreground">Manage your account and preferences</p>
      </div>

      <div className="flex-1 overflow-auto p-6 space-y-6">
        <Card className="max-w-md">
          <CardHeader>
            <CardTitle className="text-xl">{t('settings.profile')}</CardTitle>
            <CardDescription>Edit your profile information</CardDescription>
          </CardHeader>
          <CardContent>
            <div className="mb-6 flex flex-col items-center">
              <div className="relative mb-3">
                <Avatar className="h-20 w-20">
                  <img src={getAvatarUrl(previewAvatar || user?.avatarUrl, user?.username || "")} alt={user?.username} />
                  <AvatarFallback>{getInitials(user?.username || "")}</AvatarFallback>
                </Avatar>
                <button
                  onClick={() => fileInputRef.current?.click()}
                  className="absolute bottom-0 right-0 bg-primary text-primary-foreground rounded-full p-1.5 hover:bg-primary/90 transition-colors"
                  type="button"
                  data-testid="button-upload-avatar"
                >
                  <Upload className="h-4 w-4" />
                </button>
              </div>
              <input
                ref={fileInputRef}
                type="file"
                accept="image/*"
                onChange={handleAvatarChange}
                className="hidden"
                data-testid="input-avatar-file"
              />
              {previewAvatar && (
                <Button
                  type="button"
                  variant="ghost"
                  size="sm"
                  onClick={clearAvatar}
                  className="text-destructive hover:text-destructive mb-2"
                  data-testid="button-clear-avatar"
                >
                  <X className="h-4 w-4 mr-1" />
                  Clear
                </Button>
              )}
              <p className="text-xs text-muted-foreground">Click camera to upload (max 5MB)</p>
            </div>

            <Form {...form}>
              <form onSubmit={handleSubmit} className="space-y-4">
                <FormField
                  control={form.control}
                  name="displayName"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('settings.displayName')}</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Your display name (optional)"
                          data-testid="input-display-name"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="username"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>{t('settings.username')}</FormLabel>
                      <FormControl>
                        <Input
                          {...field}
                          placeholder="Your username"
                          data-testid="input-username"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button
                  type="submit"
                  className="w-full"
                  disabled={updateProfileMutation.isPending || !form.formState.isDirty}
                  data-testid="button-update-profile"
                >
                  {updateProfileMutation.isPending ? "Saving..." : t('settings.save')}
                </Button>
              </form>
            </Form>
          </CardContent>
        </Card>

        <Card className="max-w-md">
          <CardHeader>
            <CardTitle className="text-xl">Preferences</CardTitle>
            <CardDescription>Customize your experience</CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="space-y-2">
              <Label htmlFor="theme" className="text-sm font-medium">{t('settings.theme')}</Label>
              <ThemeToggle />
            </div>

            <div className="space-y-2">
              <Label htmlFor="language" className="text-sm font-medium">{t('settings.language')}</Label>
              <LanguageToggle />
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

